"use strict";
var parse = require("tailwindcss/lib/value-parser/parse");
var walk = require("tailwindcss/lib/value-parser/walk");
var stringify = require("tailwindcss/lib/value-parser/stringify");
function ValueParser(value) {
    if (this instanceof ValueParser) {
        this.nodes = parse(value);
        return this;
    }
    return new ValueParser(value);
}
ValueParser.prototype.toString = function() {
    return Array.isArray(this.nodes) ? stringify(this.nodes) : "";
};
ValueParser.prototype.walk = function(cb, bubble) {
    walk(this.nodes, cb, bubble);
    return this;
};
ValueParser.unit = require("tailwindcss/lib/value-parser/unit");
ValueParser.walk = walk;
ValueParser.stringify = stringify;
module.exports = ValueParser;
